schemas:
DEFINE TABLE analytics_item SCHEMAFULL;

DEFINE FIELD key ON analytics_item TYPE string;
DEFINE FIELD date ON analytics_item TYPE datetime DEFAULT time::now();
DEFINE FIELD score ON analytics_item TYPE number DEFAULT 0;

DEFINE INDEX analyticsKeyIndex ON analytics_item COLUMNS key, date UNIQUE;
DEFINE TABLE audit_log SCHEMALESS;

DEFINE FIELD guild_id ON audit_log TYPE string;

DEFINE FIELD originator_id ON audit_log TYPE string;
DEFINE FIELD event_name ON audit_log TYPE string;
DEFINE FIELD created_at ON audit_log TYPE datetime DEFAULT time::now();
DEFINE TABLE refresh_token_blacklist SCHEMAFULL;

DEFINE FIELD expires ON refresh_token_blacklist TYPE datetime;
DEFINE TABLE bot_data SCHEMAFULL;

DEFINE FIELD value ON bot_data TYPE any;

DEFINE INDEX botDataIndex ON bot_data COLUMNS key UNIQUE;
DEFINE TABLE channel_config SCHEMALESS;

DEFINE FIELD guild_id ON channel_config TYPE string;
DEFINE FIELD channel_id ON channel_config TYPE string;

DEFINE FIELD type ON channel_config TYPE string;

DEFINE FIELD created ON channel_config TYPE datetime DEFAULT time::now();
DEFINE TABLE feeddata SCHEMAFULL;

DEFINE FIELD url ON feeddata TYPE string;
DEFINE FIELD name ON feeddata TYPE string;
DEFINE TABLE fflag SCHEMAFULL;

DEFINE FIELD type ON fflag TYPE number DEFAULT 0;
DEFINE FIELD value ON fflag TYPE any DEFAULT "";

DEFINE FIELD created ON fflag TYPE datetime DEFAULT time::now();
DEFINE FIELD updated ON fflag TYPE datetime VALUE time::now() DEFAULT time::now();

DEFINE INDEX fflagNameIndex ON TABLE fflag COLUMNS id SEARCH ANALYZER ascii BM25 HIGHLIGHTS;
DEFINE TABLE giveaway SCHEMAFULL;

DEFINE FIELD guild_id ON giveaway TYPE string;
DEFINE FIELD channel_id ON giveaway TYPE string;
DEFINE FIELD message_id ON giveaway TYPE string;

DEFINE FIELD ended ON giveaway TYPE bool DEFAULT false;
DEFINE FIELD ends_at ON giveaway TYPE datetime;

DEFINE FIELD prize ON giveaway TYPE string;
DEFINE FIELD hosted_by ON giveaway TYPE string;

DEFINE FIELD winner_amount ON giveaway TYPE number;
DEFINE FIELD winners ON giveaway TYPE array<string>;
DEFINE FIELD winners.* ON giveaway TYPE string;

DEFINE FIELD entries ON giveaway TYPE array<string>;
DEFINE FIELD entries.* ON giveaway TYPE string;
DEFINE TABLE guild SCHEMALESS;

DEFINE FIELD premium ON guild TYPE string DEFAULT '0';
DEFINE FIELD subscription_id ON guild TYPE option<string>;

DEFINE FIELD active ON guild TYPE bool DEFAULT true;
DEFINE FIELD last_active ON guild TYPE datetime VALUE time::now() DEFAULT time::now();

DEFINE FIELD name ON guild TYPE string;
DEFINE FIELD bio ON guild TYPE option<string>;
DEFINE FIELD avatar ON guild TYPE option<string>;
DEFINE FIELD members ON guild TYPE number DEFAULT 1;

DEFINE FIELD first_seen ON guild TYPE datetime DEFAULT time::now();

DEFINE INDEX guildNameIndex ON TABLE guild COLUMNS name SEARCH ANALYZER ascii BM25 HIGHLIGHTS;
DEFINE INDEX guildBioIndex ON TABLE guild COLUMNS bio SEARCH ANALYZER ascii BM25 HIGHLIGHTS;
DEFINE TABLE guild_user SCHEMALESS;

DEFINE FIELD guild_id ON guild_user TYPE string;
DEFINE FIELD user_id ON guild_user TYPE string;
DEFINE FIELD perms ON guild_user TYPE string DEFAULT "";
DEFINE FIELD xp ON guild_user TYPE number DEFAULT 0;
DEFINE FIELD can_access_dash ON guild_user TYPE bool DEFAULT false;
DEFINE FIELD is_banned ON guild_user TYPE bool DEFAULT false;
DEFINE FIELD bypass_verification ON guild_user TYPE bool DEFAULT false;
DEFINE FIELD roles ON guild_user TYPE array<number> DEFAULT [];

DEFINE FIELD join_date ON guild_user TYPE datetime DEFAULT time::now();

DEFINE INDEX guildUserIndex ON guild_user COLUMNS guild_id, id UNIQUE;
DEFINE TABLE image SCHEMALESS;

DEFINE FIELD source_url ON image TYPE string;
DEFINE FIELD data ON image TYPE string;
DEFINE FIELD expires ON image TYPE datetime;
DEFINE TABLE role_config SCHEMALESS;

DEFINE FIELD guild_id ON role_config TYPE string;
DEFINE FIELD role_id ON role_config TYPE string;

DEFINE FIELD type ON role_config TYPE string;

DEFINE FIELD created ON role_config TYPE datetime DEFAULT time::now();
DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE user_token SCHEMALESS;

DEFINE FIELD type ON user_token TYPE string;
DEFINE FIELD created ON user_token TYPE datetime DEFAULT time::now();
DEFINE TABLE user SCHEMALESS;

DEFINE FIELD avatar ON user TYPE string;
DEFINE FIELD name ON user TYPE string;
DEFINE FIELD language ON user TYPE string DEFAULT "en";

DEFINE FIELD created ON user TYPE datetime DEFAULT time::now();
DEFINE FIELD updated ON user TYPE datetime VALUE time::now() DEFAULT time::now();

DEFINE INDEX userNameIndex ON TABLE user COLUMNS name SEARCH ANALYZER ascii BM25 HIGHLIGHTS;
DEFINE TABLE user_identifier SCHEMALESS;

DEFINE FIELD connections ON user_identifier FLEXIBLE TYPE object;
DEFINE FIELD uses_vpn ON user_identifier TYPE bool DEFAULT false;
DEFINE FIELD hashed_ip ON user_identifier TYPE option<string>;
DEFINE FIELD browser_id ON user_identifier TYPE option<string>;
DEFINE TABLE user_status SCHEMALESS;

DEFINE FIELD guild_id ON user_status TYPE string;
DEFINE FIELD user_id ON user_status TYPE string;
DEFINE FIELD type ON user_status TYPE string;

DEFINE FIELD created ON user_status TYPE datetime DEFAULT time::now();
DEFINE FIELD ends ON user_status TYPE option<datetime>;
